            // 결과를 표시할 element
            const resultElement = document.getElementById("result");

            // 현재 화면에 표시된 값
            let number = resultElement.innerText;

            // 결과 출력
            resultElement.innerText = number;
        };

        return (
            <div className="Shop_App">
