import Web3 from "web3";
import fs from "fs";
import path from "path";

const basePath = "/Users/yuhojin/Desktop/BEB_01_final2-contract";

const web3 = new Web3(
  "https://ropsten.infura.io/v3/7d51c56ec18d4181b7c80d509293fc37"
);

const character_json = fs.readFile(
  path.join(basePath, "/truffle/abi/Character.json").toString(),
  {
    encoding: "utf-8",
  }
);

const character_abi = JSON.parse(character_json).abi;

const character_CA = fs.readFile(
  path.join(basePath, "/truffle/CA/character_CA").toString(),
  {
    encoding: "utf-8",
  }
);

export const Character_contract = async () => {
  const Contract = await new web3.eth.Contract(character_abi, character_CA);
  return Contract.methods;
};
