import axios from "axios";
import React, { useEffect, useState } from "react";
import "./Buy.scss";
function Buy({ player }) {
  console.log(player);
  const [eth, SetEth] = useState(0);
  const [Token, SetToken] = useState(0);
  const [eth_to_token, Set_eth_to_token] = useState(true);

  const changeState = () => {
    Set_eth_to_token(!eth_to_token);
  };

  const change_balance = (Token_name, e) => {
    if (Token_name === "token") {
      SetToken(e.target.value);
    } else {
      SetEth(e.target.value);
    }
  };

  const getUser_balance = async () => {
    await axios
      .post("http://localhost:8080/swap", {
        address: player.address,
      })
      .then((result) => {
        console.log(result);
      });
  };

  useEffect(() => {
    getUser_balance();
  }, [player]);
  return (
    <div className="Buy_App">
      <div className="Buy_container">
        <div className="Buy_layout">
          <div className="Buy_title">
            <div onClick={changeState} className="change_button">
              {eth_to_token ? "ETH -> Token" : "Token -> ETH"}
            </div>
            <div>Eth To Token!!</div>
          </div>
          {eth_to_token ? (
            <>
              <input
                type="number"
                className="Buy_eth"
                placeholder="eth.."
                onChange={(e) => change_balance("eth", e)}
              />
              <input
                type="number"
                className="Buy_eth"
                placeholder="Gold.."
                onChange={(e) => change_balance("token", e)}
              />
            </>
          ) : (
            <>
              <input
                type="number"
                className="Buy_eth"
                placeholder="Gold.."
                onChange={(e) => change_balance("token", e)}
              />{" "}
              <input
                type="number"
                className="Buy_eth"
                placeholder="eth.."
                onChange={(e) => change_balance("eth", e)}
              />{" "}
            </>
          )}

          <button className="Buy_button">스왑!</button>
        </div>
        <div className="balance">Your_balance_eth : 0 </div>
        <div className="balance">Your_balance_Token : 0 </div>
      </div>
    </div>
  );
}

export default Buy;
